import React from 'react';

const PostDetail = () => {
  return (
    <div className="justify-between p-5 sm:p-5 cursor-pointer w-full h-full flex flex-col flex-1 shadow-secondary-button-stroke dark:shadow-secondary-button-stroke-dark hover:bg-gray-40/5 active:bg-gray-40/10  hover:dark:bg-gray-60/5 active:dark:bg-gray-60/10 rounded-2xl text-xl text-primary dark:text-primary-dark leading-relaxed">
      <div className="flex flex-row gap-3 w-full">
        <h2 className="font-semibold flex-1 text-2xl lg:text-3xl hover:underline leading-snug mb-4">
          React Canaries: Implementação Incremental de Recursos Fora da Meta
        </h2>
      </div>
      <div>
        <div className="flex flex-row justify-start gap-2 items-center text-base text-tertiary dark:text-tertiary-dark">
          3 de maio de 2023
        </div>
        <span className="text-base text-secondary dark:text-secondary-dark">
          <p className="whitespace-pre-wrap my-4">
            Tradicionalmente, novos recursos do React costumavam estar disponíveis apenas primeiro na Meta e chegavam às versões de código aberto posteriormente. Gostaríamos de oferecer à comunidade React a opção de adotar novos recursos individuais assim que seu design estiver próximo da finalização, de forma semelhante à maneira como a Meta utiliza o React internamente. Estamos introduzindo um novo canal oficialmente suportado de lançamento, chamado Canary. Ele permite que configurações selecionadas, como frameworks, desvinculem a adoção de recursos individuais do React do cronograma de lançamento do React.
          </p>
        </span>
        <div className="text-link text-base dark:text-link-dark hover:underline mt-4">
          Leia mais
        </div>
      </div>
    </div>
  );
};

export default PostDetail;
